,p_name=>'promotions'
,p_alias=>'MAP'
,p_step_title=>'Promotions'
,p_reload_on_submit=>'A'
,p_warn_on_unsaved_changes=>'N'
,p_autocomplete_on_off=>'ON'
,p_javascript_file_urls=>'https://maps.googleapis.com/maps/api/js?key=&P10_MAPS_API_KEY.'
,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'var htmldb_delete_message=''"DELETE_CONFIRM_MSG"'';',
'if($v(''P10_MAPS_API_KEY'') > ''''){',
'    (function($, gm) {',
'        var map = null;',
'        var boundsChanging = false;',
'        var regionChanging = true;',
'        var markers = {}; // map of region_id to array of stores { name: "", lat: n, lng: n, ...}',
'',
'        // Customize map style here.',
'        // Use map style editor here: http://gmaps-samples-v3.googlecode.com/svn/trunk/styledmaps/wizard/index.html',
'        var mapStyle = [',
'          {',
'            "elementType": "geometry",',
'            "stylers": [',
'              { "saturation": 29 },',
'              { "lightness": -1 },',
'              { "gamma": 0.93 }',
'            ]',
'          },{',
'            "elementType": "labels",',
'            "stylers": [',
'              { "weight": 0.1 }',
'            ]',
'          },{',
'            "featureType": "water",',
'            "stylers": [',
'              { "saturation": 67 }',
'            ]',
'          },{',
'            "featureType": "road",',
'            "stylers": [',
'              { "visibility": "simplified" },',
'              { "gamma": 1.22 }',
'            ]',
'          },{',
'          }',
'        ]',
'',
'        // map of store type to icon',
'        // icon assume a sprite with square icon and shadow laid out horizontally with icon first.',
'        var markerImageMap = {',
'            "Competitor": { url: "#APP_IMAGES#blue_marker.png", h: 36, w: 24, sw: 40 },',
'            "Branch": { url: "#APP_IMAGES#red_marker.png", h: 36, w: 24, sw: 40 }',
'        };',
'',
'        function initialize() {',
'            var i, r, mi, ',
'                zoom = $("#P10_ZOOM").val() * 1,',
'                lat = $("#P10_LAT").val() * 1,',
'                lng = $("#P10_LNG").val() * 1,',
'                mapOptions = {',
'                  center: new gm.LatLng(lat, lng),',
'                  zoom: zoom,',
'                  mapTypeId: google.maps.MapTypeId.ROADMAP,',
'                  styles: mapStyle,',
'                  mapTypeControl: false,',
'                  scaleControl: false,',
'                  streetViewControl: false,',
'                  overviewMapControl: false',
'                };',
'            map = new gm.Map($("#mapPlaceholder").get(0), mapOptions);',
'            // init regions',
'            for (i = 0; i < g_mapAllRegions.length; i++) {',
'                r = g_mapAllRegions[i];',
'                r.loc = new gm.LatLng(r.lat, r.lng);',
'                delete r.lat;',
'                delete r.lng;',
'            }',
'            // init marker images',
'            for (i in markerImageMap) {',
'                mi = markerImageMap[i];',
'                if (mi) {',
'                  markerImageMap[i] = [',
'                      new gm.MarkerImage(mi.url, new gm.Size(mi.w, mi.h), new gm.Point(0,0), new gm.Point(mi.w/2,mi.h)),',
'                      new gm.MarkerImage(mi.url, new gm.Size(mi.sw, mi.h), new gm.Point(mi.w, 0), new gm.Point(mi.w/2,mi.h))',
'                  ];',
'                }',
'            }',
'',
'            gm.event.addListener(map, ''bounds_changed'', function() {',
'                var i, r, rfound = [],',
'                    zoom = map.getZoom(),',
'                    bounds = map.getBounds(),',
'                    origRegion = $("#P10_REGION").val();',
'',
'                boundsChanging = true;',
'                // avoid getting everything at once',
'                if (zoom >= 6) {',
'                    for (i = 0; i < g_mapAllRegions.length; i++) {',
'                        r = g_mapAllRegions[i];',
'                        if (bounds.contains(r.loc)) {',
'                            rfound.push(r);',
'                            if (!markers[r.id]) {',
'                                // todo: sync ajax is very bad here! Find a better way',
'                                $("#P10_REGION").val(r.id).change(); // trigger a DA',
'                            }',
'                        }',
'                    }',
'                    if (regionChanging) {',
'                        $("#P10_REGION").val(origRegion);',
'                    } else if (rfound.length === 1) {',
'                        if ($("#P10_REGION").val() != rfound[0].id) {',
'                            $("#P10_REGION").val(rfound[0].id);',
'                        }',
'                    }',
'                }',
'                boundsChanging =false;',
'                regionChanging = false;',
'            });',
'        }',
'        window.getMapCenter = function() {',
'            var loc = map.getCenter();',
'            console.log("Create region with this information: lat/lng=" + loc + ", zoom=" + map.getZoom());',
'        };',
'        window.setMapCenter = function() {',
'            var zoom = $("#P10_ZOOM").val() * 1,',
'                lat = $("#P10_LAT").val() * 1,',
'                lng = $("#P10_LNG").val() * 1,',
'                storeLat = $("#P10_STORE_LAT").val(),',
'                storeLng = $("#P10_STORE_LNG").val();',
'            if (boundsChanging) {',
'                return;',
'            }',
'            regionChanging = true; // reset after bounds change',
'            if (storeLat !== "" && storeLng !== "") {',
'                zoom = 16;',
'                lat = storeLat * 1;',
'                lng = storeLng * 1;',
'                $("#P10_STORE_LAT").val("");',
'                $("#P10_STORE_LNG").val("");',
'            }',
'            map.setCenter(new gm.LatLng(lat, lng));',
'            map.setZoom(zoom);',
'        };',
'',
'        function getIcon(type) {',
'            var mi = markerImageMap[type];',
'            if (mi) {',
'                return mi[0];',
'            }',
'            return null;',
'        }',
'',
'        function getShadow(type) {',
'            var mi = markerImageMap[type];',
'            if (mi) {',
'                return mi[1];',
'            }',
'            return null;',
'        }',
'',
'        function markerClicked(evt) {',
'          $("#P10_STORE_ID").val(this.store.id).change(); // trigger a DA',
'        }',
'',
'        window.addMapMarkers = function() {',
'          var prevStores, i, store,',
'              stores = JSON.parse($("#P10_STORES").val()),',
'              regionId = $("#P10_REGION").val();',
'',
'          prevStores = markers[regionId];',
'          if (prevStores) {',
'            for (i = 0; i < prevStores.length; i++) {',
'              store = prevStores[i];',
'              store.marker.setMap(null);',
'            }',
'          }',
'          stores = stores.stores;',
'          markers[regionId] = stores; // replace old with new',
'          for (i = 0; i < stores.length; i++) {',
'            store = stores[i];',
'            store.marker = new gm.Marker({',
'              position: new gm.LatLng(store.lat, store.lng),',
'              map: map,',
'              icon: getIcon(store.type),',
'              shadow: getShadow(store.type),',
'              title: store.name',
'              // THINK other options drop animation, custom icons (ex: depend on store type)',
'            });',
'            store.marker.store = store;',
'            gm.event.addListener(store.marker, ''click'', markerClicked);',
'          }',
'        }',
