%spark

// BEFORE RUNNING THIS, YOU WILL NEED TO EDIT THIS
//  1.Insert your database Connect String
//  2.Insert your database user name
//  3.Insert your database password


//define URL for the Oracle JDBC driver
println(">>>>>>>Defining url for Oracle JDBC")
val url="jdbc:oracle:thin:@//" + "CD-DBCS-ORCL12:1521/PDB1.gse00010212.oraclecloud.internal"

//define the username and password as properties
println(">>>>>>>Defining Oracle JDBC username and password")
val prop = new java.util.Properties
prop.setProperty("user","scott")
prop.setProperty("password","tiger")
prop.setProperty("driver","oracle.jdbc.OracleDriver") //the driver is needed to be defined with Spark 1.6.1 due to https://issues.apache.org/jira/browse/SPARK-14204

//now you can use JDBC commands like: val movies = sqlContext.read.jdbc(url,"movie",prop)
val emp = sqlContext.read.jdbc(url,"emp",prop)
//emp.explain()
emp.printSchema()
emp.show()

//register the emp dataframe as a SparkSQL table
emp.registerTempTable("emp_sparksql")

//we can also do specific queries like the following (note that we write our query as if it was a subquery in the FROM section of a select statement)
val emp_query = sqlContext.read.jdbc(url, "(select e.deptno, d.dname, count(*) dcount from emp e, dept d where e.deptno=d.deptno group by e.deptno, d.dname) eq", prop)
emp_query.show()
//emp_query.explain()

println("done")

%spark

println(">>>>>>>Setting up workaround for SPARK-12941, etc")
//based on http://stackoverflow.com/questions/31287182/writing-to-oracle-database-using-apache-spark-1-4-0

  import org.apache.spark.sql.jdbc.{JdbcDialects, JdbcType, JdbcDialect}
  import org.apache.spark.sql.types._
  import java.sql.Types

  val OracleDialect = new JdbcDialect {
    override def canHandle(url: String): Boolean = url.startsWith("jdbc:oracle") 

    override def getCatalystType(
      sqlType: Int, typeName: String, size: Int, md: MetadataBuilder): Option[DataType] = {
    // Handle NUMBER fields that have no precision/scale in special way
    // because JDBC ResultSetMetaData converts this to 0 procision and -127 scale
    // For more details, please see
    // https://github.com/apache/spark/pull/8780#issuecomment-145598968
    // and
    // https://github.com/apache/spark/pull/8780#issuecomment-144541760
    if (sqlType == Types.NUMERIC && size == 0) {
      // This is sub-optimal as we have to pick a precision/scale in advance whereas the data
      //  in Oracle is allowed to have different precision/scale for each value.
      Option(DecimalType(DecimalType.MAX_PRECISION, 10))
    } else {
      None
    }
    }

   override def getJDBCType(dt: DataType): Option[JdbcType] = dt match {
    // For more details, please see
    // https://docs.oracle.com/cd/E19501-01/819-3659/gcmaz/
    case BooleanType => Some(JdbcType("NUMBER(1)", java.sql.Types.BOOLEAN))
    case IntegerType => Some(JdbcType("NUMBER(10)", java.sql.Types.INTEGER))
    case LongType => Some(JdbcType("NUMBER(19)", java.sql.Types.BIGINT))
    case FloatType => Some(JdbcType("NUMBER(19, 4)", java.sql.Types.FLOAT))
    case DoubleType => Some(JdbcType("NUMBER(19, 4)", java.sql.Types.DOUBLE))
    case ByteType => Some(JdbcType("NUMBER(3)", java.sql.Types.SMALLINT))
    case ShortType => Some(JdbcType("NUMBER(5)", java.sql.Types.SMALLINT))
    case StringType => Some(JdbcType("VARCHAR2(255)", java.sql.Types.VARCHAR))
      case BinaryType => Some(JdbcType("BLOB", java.sql.Types.BLOB))
      case TimestampType => Some(JdbcType("DATE", java.sql.Types.DATE))
      case DateType => Some(JdbcType("DATE", java.sql.Types.DATE))
      case DecimalType.Unlimited => Some(JdbcType("NUMBER(38,4)", java.sql.Types.NUMERIC))
    case _ => None
   }


  }

  JdbcDialects.registerDialect(OracleDialect)
println("Finised with workaround.")


// review our emp_query data_frame (created above)
println("Reviewing our emp_query data frame...")
emp_query.show()
emp_query.printSchema()


import org.apache.spark.sql.SaveMode
//possible SaveModes are SaveMode.Append, SaveMode.Overwrite, SaveMode.ErrorIfExists, SaveMode.Ignore


println("Writing Spark DataFrame to Oracle Database")
emp_query.write
   .mode(SaveMode.Overwrite)
   .jdbc(url,"EMP_QUERY",prop)


//sometimes you might hit errors writing back to Oracle due to datatypes.  Here is how you can see the datatypes being generated for Oracle and look for issues
import org.apache.spark.sql.execution.datasources.jdbc.JdbcUtils
println("Generated Oracle Datatypes: "+JdbcUtils.schemaString(emp_query, url))

//your Spark dataframe needs to use valid Oracle column names (i.e. no spaces, no reserved words, etc).  If you need to rename dataframe fields, you can do operations like this
//val newdDF=oldDF.withColumnRenamed("Birth Year","BirthYear")

//unregister our custom Dialect just to be clean
JdbcDialects.unregisterDialect(OracleDialect)

println("done")
